mapper.insert:
INSERT INTO WEBTOON
(
	 WEBTOON_IDX
	,WEBTOON_TITLE
	,WEBTOON_CONTENT
	,WEBTOON_SUMMARY
	,WEBTOON_AUTHOR
	,THUM
	,IN_DATE
	,IN_ADMIN
	,USE_YN
	)
	VALUES
(
	 WEBTOON_IDX_SEQ.NEXTVAL
	,#{webtoon_title}
	,#{webtoon_content}
	,#{webtoon_summary}
	,#{webtoon_author}
	,#{thum}
	,SYSDATE
	,#{in_admin}
	,#{use_yn}
)

mapper.update:
UPDATE WEBTOON SET
 WEBTOON_TITLE 	 = #{webtoon_title}
,WEBTOON_CONTENT = #{webtoon_content}
,WEBTOON_SUMMARY = #{webtoon_summary}
,WEBTOON_AUTHOR  = #{webtoon_author}
,THUM 		 	 = #{thum}
,UP_DATE 		 = SYSDATE
,UP_ADMIN 		 = #{up_admin}
,USE_YN 		 = #{use_yn}
WHERE
WEBTOON_IDX = #{webtoon_idx}

mapper.delete:
UPDATE WEBTOON
SET THUM = NULL
WHERE WEBTOON_IDX = #{webtoon_idx}
