mapper.insert:
INSERT INTO FILE_DB
(
   FILE_IDX
  ,FILE_TYPE
  ,PA_IDX
  ,ORI_NAME
  ,SV_NAME
  ,IN_DATE
  ,IN_USER
  ,IN_IP
)
VALUES
(
   FILE_IDX_SEQ.NEXTVAL
  ,#{file_type}
  ,#{pa_idx}
  ,#{ori_name}
  ,#{sv_name}
  ,SYSDATE
  ,#{in_user}
  ,#{in_ip}
)

mapper.delete:
DELETE 
FROM FILE_DB 
WHERE PA_IDX = #{pa_idx}

AND SV_NAME = #{sv_name}
