mapper.insert:
	INSERT INTO MEMBER
	(
	  MEMBER_IDX
	 ,MEMBER_TYPE
	 ,MEMBER_ID
	 ,MEMBER_EMAIL
	 ,MEMBER_PW
	 ,MEMBER_NAME

	 ,IN_DATE
	)
	VALUES
	(
	 MEMBER_IDX_SEQ.NEXTVAL
	 ,#{member_type}
	 ,#{member_id}
	 ,#{member_email}
	 ,#{member_pw}
	 ,#{member_name}

	 ,SYSDATE
	)

mapper.select:
SELECT
	COUNT(*)
FROM MEMBER
WHERE MEMBER_ID = #{member_id}	
AND MEMBER_PW = #{member_pw}
